BASE_API = "http://localhost:3000/"

class UserAdapter{
  static login(data){
    return fetch(`${BASE_API}/${"????????????"}`,{
      method: "POST",
      header: {
        "Content-type": "application/json",
        "Accepts": "application/json"
      },
      body: JSON.stringify({user: data})
    })
    .then(res => res.json())

  }
}
