import React, { Component } from 'react';
import './App.css';

import LoginForm from './components/LoginForm'
import {Switch, BrowserRouter as Router, Route, Link} from 'react-router-dom'

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      currentUser: {},
      snacks: [],
    };
  }

  logout = () => {
    this.state = {
      currentUser: {},
      snacks: [],
    }
  }

  render() {
    return (
      <Router>
        <div className="App">
          <h1>Snacc Attacc</h1>

          {Object.keys(this.state.currentUser).length == 0 ? <Link to="/login">Login</Link> : <p onClick={this.logout}>Logout</p>} | <Link to="/">Home</Link>

          <Switch>
            <Route path="/login" component={LoginForm}></Route>
          </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
